import React, {useState} from 'react';
import { useNavigate} from 'react-router-dom';
import './styles.css';

import api from '../../services/api'

import logoImage from '../../assets/logo.svg'

export default function NewPostagem() {

    const [titulo, setTitulo] = useState('');
    const [descricao, setDescricao] = useState('');
    const [foto, setFoto] = useState('');
    const [autor, setAutor] = useState('');


    const [usuario, setUsuario] = useState('');
    const [tema, setTema] = useState('');

    const navigate = useNavigate();

    async function login(e){
        e.preventDefault();

        const data = {
            titulo,
            descricao,
            foto,
            autor,
            usuario,
            tema,
        };

        try {
            const response = await api.post('postagem', data);
            localStorage.setItem('id', '0' );
            localStorage.setItem('titulo', titulo);
            localStorage.setItem('descricao', descricao);
            localStorage.setItem('foto', foto);
            localStorage.setItem('autor', autor);
            localStorage.setItem('usuario', usuario);
            localStorage.setItem('tema', tema);
        //    localStorage.setItem('accessToken', response.data.token);

            navigate('/postagens')
            alert('Cadastrado com sucesso!')
        } catch (err) {
            alert('Nao foi possivel cadastrar');
        }
    };

    return (
        <div className="login-container">
            <section className="form">
                <img src={logoImage} alt="Erudio Logo"/>
                <form onSubmit={login}>
                    <h1>Cadastrar conta</h1>
                    <input
                        type="text" placeholder="Nome" name="Nome"
                        value={titulo}
                        onChange={e => setTitulo(e.target.value)}
                    />
                
                    <input
                        type="text" placeholder="Descricao do livro"
                        value={descricao}
                        onChange={e => setDescricao(e.target.value)}
                    />
                    <input
                        type="text" placeholder="Foto do Livro"
                        value={foto}
                        onChange={e => setFoto(e.target.value)}
                    />

                    <input
                        type="text" placeholder="Autor do Livro"
                        value={autor}
                        onChange={e => setAutor(e.target.value)}
                    />
                    <input
                        type="text" placeholder="idUsuario"
                        value={usuario}
                        onChange={e => setUsuario(e.target.value)}
                    />
                    <input
                        type="text" placeholder="idTema"
                        value={tema}
                        onChange={e => setTema(e.target.value)}
                    />

                    

                    

                    <button className="button" type="submit">Cadastrar </button>
                </form>

            </section>


        </div>
    )

}
