import React, {useState} from 'react';
import { useNavigate, Link, useParams} from 'react-router-dom';
import './styles.css';

import api from '../../services/api'

import logoImage from '../../assets/logo.svg'

export default function NewPostagem() {

    const [titulo, setTitulo] = useState('');
    const [descricao, setDescricao] = useState('');
    const [foto, setFoto] = useState('');
    const [autor, setAutor] = useState('');
    const {bookId} = useParams();

    const accessToken = localStorage.getItem('accessToken');

    const [usuario, setUsuario] = useState('');
    const [tema, setTema] = useState('');

    const navigate = useNavigate();

    async function login(e){
        e.preventDefault();

        const data = {
            titulo,
            descricao,
            foto,
            autor,
            usuario,
            tema,
        };
        try {
            if (bookId === '0') {
                await api.post('api/book/v1', data, {
                    headers: {
                        Authorization: `Bearer ${accessToken}`
                    }
                });
            } else {
                data.id = id;
                await api.put('api/book/v1', data, {
                    headers: {
                        Authorization: `Bearer ${accessToken}`
                    }
                });
            }

            navigate('/books');
        } catch (err) {
            alert('Error while recording Book! Try again!')
        }
    };

    return (
        <div className="login-container">
            <section className="form">
                <img src={logoImage} alt="Erudio Logo"/>
                <form onSubmit={login}>
                    <h1>Cadastrar conta</h1>
                    <input
                        type="text" placeholder="Nome" name="Nome"
                        value={titulo}
                        onChange={e => setTitulo(e.target.value)}
                    />
                
                    <input
                        type="text" placeholder="Descricao do livro"
                        value={descricao}
                        onChange={e => setDescricao(e.target.value)}
                    />
                    <input
                        type="text" placeholder="Foto do Livro"
                        value={foto}
                        onChange={e => setFoto(e.target.value)}
                    />

                    <input
                        type="text" placeholder="Autor do Livro"
                        value={autor}
                        onChange={e => setAutor(e.target.value)}
                    />
                   
                    

                    

                    <button className="button" type="submit">Cadastrar </button>
                </form>

            </section>


        </div>
    )

}
